import styled from 'styled-components';

// Type Scale: Golden Tritonic
// ----------
// f0 = 1em = 17px
// r = 1.618034
// n = 3

export const LargeHeader = styled.h1`
	margin-top: 1rem;
	font-size: 1.618rem;
	font-weight: 700;
	font-style: normal;
	line-height: 1.25;
	color: rgb(88, 96, 110); 
`;

export const MediumHeader = styled.h2`
	margin-top: 1rem;
	font-size: 1.372rem;
	font-weight: 700;
	font-style: normal;
	line-height: 1.25;
	color: rgb(88, 96, 110);
`;

export const SmallHeader = styled.h3`
	margin-top: 1rem;
	font-size: 1.174rem;
	font-weight: 700;
	font-style: normal;
	line-height: 1.25;
	color: rgb(88, 96, 110);
`;

export const Paragraph = styled.p`
	margin-top: 1rem;
	font-size: 1rem;
	font-weight: 400;
	font-style: normal;
	line-height: 1.5;
	color: rgb(51, 56, 64);
`;

export const CaptionBold = styled.p`
	margin-top: 1rem;
	font-size: .852rem;
	font-weight: 700;
	line-height: 1.25;
	color: rgb(51, 56, 64);
`;

export const CaptionLight = styled.p`
	margin-top: 1rem;
	font-size: .852rem;
	font-weight: 400;
	line-height: 1.25;
	letter-spacing: -.01em;
	color: rgb(88, 96, 110);
`;
